#define VO void
#define I1 int
#define I2 ivec2
#define F1 float
#define F2 vec2
#define F3 vec3
#define F4 vec4

// check if u between 0,0 and 1,1
#define in1(u) all(lessThan(abs(u - .5), F2(.5)))

const F1
by16 = 1. / 16.;

// print at pixel n character c at position p in size s at coord n
F4
pC(F2 n, F2 p, F2 s, F2 c) {

    p = (n - p) / s;

    return in1(p)
        ? texture(iChannel0, (c + p) * by16)
        : F4(0);
}

F4
pN(F2 n, F2 p, F2 s, F1 num) {

    F2
    c = 0. <= num && num <= 9.
        ? F2(num, 12.)
        : 10. <= num && num <= 16.
            ? F2(num - 9., 11.)
            : F2(0, 13);

    return pC(n, p, s, c);
}


VO
mainImage(out F4 o, F2 v) {

	F2
    r   = iResolution.xy,
    s   = 1. / r,
    t   = s * F2(r.x * s.y, 1),
    u   =               v * t,
    m   = clamp(iMouse.xy * t, F2(0), F2(15. * by16)),
    u16 = floor(16. * u),
    m16 = floor(16. * m);

    o = in1(u)
        ? (all(equal(u16, m16))
              ? pN(u, u16 * by16, F2(.5 * by16, by16), floor(15. - m16.y)).xxxx +
                pN(u, (u16 + F2(.5,.0)) * by16, F2(.5 * by16, by16), floor(m16.x)).xxxx
              : pC(u, u16 * by16, F2(by16), u16).xxww
          )
        : pC(u, F2(1., 0.), F2((r.x - r.y) * s.x * r.x * s.y), m16);
}
