float ease( float x ) {                                 // rewritten from https://shadertoy.com/view/3ddBWB
	return x > .5 ? x = 1.-x, 1. - 4.* x*x*x            // stepier than smoothstep or .5-.5*cos
                  :                4.* x*x*x ;
}

#define B(x)  mod( floor(x), 2. )

void mainImage( out vec4 O, vec2 u )
{   
    O-=O; 
    vec2 R = iResolution.xy,
         U = u *= 8. / R.y;
    if (U.x > 8.) { O += .5; return; }                  // out of board

    float T = fract(iTime/2.), t = ease( T ), f,
          n = B(iTime/2.);

    if ( n > 0. ) U.x += t;
    U = B(U);
    f = float( U.x == U.y );                          // checker cell color

    T = cos(3.14*T);
    O += n > 0. ? f : mix( 1.-f, f, .5-.5*sign(T)*pow(abs(T),length(u-4.)/2.) );
}
