const float period = 30.;
const float tau = 3.14159 * 2.;

float random(float i, float seed) {
	return mod(sin(354.6346456 +
            (i * 634.73453734) +
            (seed * 264.246345346)
           ) * 53467.34523463, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float h = uv.x + 1.;
    float f = (uv.y - 0.5) / h;
    f *= 4.;
    //float f = uv.y + uv.y / (uv.x + 1.) - 0.5;

    fragColor = vec4(1.);

    float t = mod(iTime, period);
    float at = t / period * tau;
    for (float i = 0.; i < 100.; ++i) {
        float centre = random(i, 235.346345) * 5. - 2.5
            + sin(at + random(i, 8934.8254645) * tau) * 0.3;
        float r = random(i, 834.183245645) * 0.6 + 0.05;
        float start = random(i, 362.934564) * period;
        if (fract((t - start + period) / period) < 0.25) {
            float rNow = r * sin(tau * (t - start) / period * 2.);
            if (f < centre + rNow && f > centre - rNow) {
                float c = mod(i, 4.);
                if (c == 0.) fragColor = vec4(1.,.78,.34,1.);
                else if (c == 1.) fragColor = vec4(.07,.6,.64,1.);
                else if (c == 2.) fragColor = vec4(1.);
                else fragColor = vec4(.29,.25,.45,1.);
            }
        }
    }
}
