float absolut(vec3 a){
    return sqrt(a.x*a.x+a.y*a.y+a.z*a.z);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float radius=50.75;
    float ebene=39.;
    float ebene2=38.;
    vec3 mid=vec3(965.0,545.0,.0);
    vec3 point=vec3(floor(floor(fragCoord.x)/10.0)*10.0+5.0,floor(floor(fragCoord.y)/10.0)*10.0+5.0,ebene*10.0);
    vec3 point2=vec3(floor(floor(fragCoord.x)/10.0)*10.0+5.0,floor(floor(fragCoord.y)/10.0)*10.0+5.0,ebene2*10.0);


    fragColor = vec4(1.0,1.0,1.0,1.0);



    if(floor(fragCoord.x)>460.0&&floor(fragCoord.x)<1470.0
      &&floor(fragCoord.y)>40.0&&floor(fragCoord.y)<1050.0){
        fragColor = vec4(.0,.0,1.0,1.0);
    }

    if(absolut(mid-point2)<radius*10.0){
        fragColor = vec4(0.9,0.8,1.0,1.0);
    }

    if(absolut(mid-point)<radius*10.0){
        fragColor = vec4(0.0,.7,0.0,1.0);
    }

    if(floor(fragCoord.x)>960.0&&floor(fragCoord.x)<970.0
      &&floor(fragCoord.y)>540.0&&floor(fragCoord.y)<550.0){
        fragColor = vec4(1.0,0.0,0.0,1.0);
    }

    if(mod(floor(fragCoord.x),10.0)==0.0){
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }
    if(mod(floor(fragCoord.y),10.0)==0.0){
        fragColor = vec4(0.0,0.0,0.0,1.0);
    }







}
