#define TAU 6.28318530718

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

float sdLine( in vec2 p, in vec2 a, in vec2 b )
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float scale = 40.;
    vec3 color = vec3(0);

    float bands = 3.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord*2.0 - iResolution.xy)/iResolution.y;
	uv *= scale;
    //uv.y += .5;



    //uv.y -= sin(iTime + uv.x*.1)*4.0;
    //uv.y=fract(((uv.y + 2.0)/4.0))*4.0-2.0;

    float x2 = ((uv.x + 2.0)/4.0);

    float i = floor(x2);

    uv.y+=sin(i*TAU/bands + iTime*.5 + i*.1)*scale;
    //uv.y+=floor(sin(i+iTime*.5)*2.)/2.*scale;


    uv.x=fract(x2)*4.0-2.0;



    uv.x = abs(uv.x);
    float d = sdLine(uv, vec2(-1), vec2(.5,.5));

    //color.r = (1.0-d*20.);


    d = abs(d-.5);
    //color.r = fract((1.0-d*5.));

    //d = abs(d-.4);
    //color.g = (1.0-d*20.);
    //d = abs(d-.3);
    //color.b = (1.0-d*20.);


   color = hsv2rgb(vec3(fract(d+iTime + i/bands), 1.0, 1.0-d*2.0));


    //color.r = fract(cos(1.*i*i)+13.3535*i);
    vec2 coord = fract(uv);
    d = min(coord.x, coord.y);

    //color+= clamp((1.0-d*10.),0.,1.);

