const float PI = 3.14159;
const float TAU = PI+PI;
const float G = 0.8;
const float M = .2;

// iq
float noise( in vec3 x )
{
    vec3 p = floor(x);
    vec3 f = fract(x);
	f = f*f*(3.0-2.0*f);

	vec2 uv = (p.xy+vec2(37.0,17.0)*p.z) + f.xy;
    vec2 rg = textureLod( iChannel0, (uv+ 0.5)/256.0, 0. ).yx;

	return -1.0+2.0*mix( rg.x, rg.y, f.z );
}

float map4( in vec3 x ) {
	return .5*noise(x) + .25*noise(x*2.) + .125*noise(x*4.) + .0625*noise(x*8.);
}

vec2 getPosition(float i, float t) {
    vec2 h = hash21(i);
    float a = (h.x*2.-1.)*PI;
    vec2 v = vec2(sin(a),cos(a))*(.5+h.y*4.);

    float x = v.x*t;
    float y = v.y*t - pow(G*M*t,2.)*.5;
	return vec2(x,y);
}

