float f(float x,float y,float t)
{
    return sin(sin(x*t+(1.-t)*y) + cos(y*t+x*(1.-t))) - cos(sin(x*y)+ cos(x));
}

vec2 gradF(float x,float y,float t)
{
    return vec2 (cos(sin(t * x + (1. - t) * y) + cos((1. - t) * x + t * y)) * (t * cos(t * x + (1. - t) * y) - (1. - t) * sin((1. - t) * x + t * y)) + (y * cos(x * y) - sin(x)) * sin(sin(x * y) + cos(x)),
                 cos(sin(t * x + (1. - t)* y) + cos((1. - t)* x + t* y))* ((1. - t) *cos(t* x + (1. - t)* y) - t* sin((1. - t) *x + t* y)) + x* cos(x* y)* sin(sin(x* y) + cos(x)));
}   
float distIsosurface(float x,float y,float t)
{
   return abs(f(x,y,t))/length(gradF(x,y,t));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = 4.*3.1415;
    // Normalized pixel coordinates (from -a to a)
    vec2 uv = (fragCoord/iResolution.xy)*vec2(2.*a,2.*a) - vec2(a,a);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    float x = uv.x;
    float y = uv.y;

    float t = cos(iTime)*0.5+0.5;

    float thickness = 0.03;

    float eps = 0.05;
    col = vec3(0,0,0);
    if ( distIsosurface(uv.x,uv.y,t) < thickness )
        col = vec3(1,1,1);



    // Output to screen
    fragColor = vec4(col,1.0);
}
