float Circle(vec2 uv, vec2 pos, float radius, float blur){ // Draw circle function

    float d = length(uv-pos);
    float ink = smoothstep(radius, radius - blur, d);

    return ink;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;     // Normalized pixel coordinates (from 0 to 1)

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

	vec3 col = vec3(0);

	float c = Circle(uv, vec2(0), .4, 0.02); //Red circle

    float mstch = Circle(uv, vec2(-.13,.05), .2, 0.01); //two blck circles
    mstch += Circle(uv, vec2(.13,.05), .2, 0.01);

    float cover = Circle(uv, vec2(.16,.12), .18, 0.01); // two red circles
    cover += Circle(uv, vec2(-.16,.12), .18, 0.01);

	c -= mstch;
    c += cover * 2.;

    col = vec3(1.,0.,0.) * c;

    // Output to screen
    fragColor = vec4(col,1.0);
}
