float ChangeRadius(){
    float r = abs(sin(iTime * 0.5))*0.4 + 0.1;
    return r;
}

vec3 ChangeColors(vec2 uv){ 
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4)); 
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float d = length(uv);
    float r = ChangeRadius();

    vec3 c = smoothstep(r, r-0.01,d) * ChangeColors(uv);

    if(c == vec3(0.0) && uv.x > 0.0)
        c = vec3(1.0);

    fragColor = vec4(c, 1.0);


}
