vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}


float PI = 3.14159265359;


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 black = vec4(0, 0, 0, 1);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * vec2(3, 2) * cos(iTime);
    uv = rotate(uv, 0.);
    uv = uv - vec2(2, 1);
    uv = rotate(uv, PI * cos(iTime / PI));
    // Complex
    vec2 z = vec2(0, 0);
    float tmp;


   	int i;
    for (i = 0; i < 50; i++) {
        tmp = z.x * z.x - z.y * z.y + uv.x;
        z.y = 2. * z.x * z.y + uv.y;
        z.x = tmp;
        if (((z.x + z.y) * (z.x + z.y)) > 9.){
            fragColor = vec4(i / 10, i / 10, i / 10, 1);
            return;
        }
    }
	fragColor = black;

}
