//Distance between a point and line in 3d space
float lineDistance(vec3 origin, vec3 dir, vec3 point)
{
    return length(cross(point - origin, dir)) / length(dir);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //normalized pixels of screen
    vec2 uv = fragCoord/iResolution.xy;
    //displaces to center of screen
	uv -= 0.5;
    //scale by resolution
    uv.x *= iResolution.x/iResolution.y;

    //origin is where the *camera is placed
    //  dir is the vector that goes from the camera to a point in the 3d world
    vec3 origin = vec3(0.0, 0.0, -2.0);
    vec3 dir = vec3(uv.x, uv.y, 0) - origin;
    // this makes the circular path that the circle moves on
    vec3 p = vec3(sin(iTime), 0.0, 1.0 + cos(iTime));
    float point = lineDistance(origin, dir, p);
    vec4 txture = texture(iChannel0, uv);
    //grabbing color values of the points from the texture
    //  to map it onto the pixel
    vec3 color = smoothstep(0.2, 0.15, point) * txture.rgb;
    fragColor = vec4(color, 1.0);
}
