float rings[4] = float[4](0.9, 0.6, 0.3, 0.1);
vec2 direction = vec2(0.0, 1.0);

float normalSine(float value) {
    return (sin(value) + 1.0) / 2.0;
}

float checker(vec2 fragCoord) {
  float radius = max(iResolution.x, iResolution.y) * 0.5;
  vec2 center = iResolution.xy * 0.5;
  float inside = 0.0;
  for(int i = 0; i < 4; i+=2) {
    float a = radius * rings[i];
    float b = radius * rings[i+1];
    float dist = length(fragCoord.xy - center);
    inside += step(dist, a) * step(b, dist);
  }
  return sign(inside);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float factor = 1.0 - ( 2.0 * checker(fragCoord));
    vec2 offsetDirection = direction * factor;
    vec2 offset = offsetDirection * normalSine(iTime);
    vec2 wrapped = fract(uv + offset);
    fragColor = texture(iChannel0, wrapped);
}
