float expStep( float x, float k, float n )
{
    return exp( -k*pow(x,n) );
}

float parabola( float x, float k )
{
    x = fract(x);
    return pow( 4.0*x*(1.0-x), k );
}
#define PI 3.141
float sinc( float x, float k )
{
    float a = PI*(k*x-1.0);
    return sin(a)/a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 distort = texture(iChannel0, uv + iTime * .3) / 32.;
    uv += distort.xy;

    vec3 col = vec3(1.,.2,.6);
    col.r = 0.5 + 0.5*cos(uv.x * 197.);
    col.r *= 1. - parabola(uv.y + (sin(uv.x*32.) + cos(uv.x*10.))*.7 + iTime * .3, 6.);
    // col.g = 0.5 + 0.5*cos(iTime+uv.y * 12.);

    //col = vec3(1.,1.,0.);
    //col *= parabola(uv.y + iTime * .3, 128.);

    // Output to screen
    fragColor = vec4(col,1.0);
}
