void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;
	vec3 pixel = vec3(0.0);

    vec2 edge2 = vec2(0.0);
    vec2 ret2 = step(edge2, r); // The 'STEP' function takes a vector
                                // Функция 'STEP' принимает вектор 

	fragColor = vec4(ret2.x,ret2.y,0.0, 1.0);
}
