vec3 star(vec2 uv, float d) {
    float n = mod(iTime, 6.);
    float g = fract(1e4*sin(n)+tan(iTime)) *.25;

    n += d*g;
    float t = min(max(abs(sin(iTime+n)*8.+g)*5., 10.), 50.);
    float r = length(uv)*t;
    float a = atan(uv.y,uv.x)+sin(g*1.5);

    float h = g*.15+0.05+(5.5/(n+1.))*0.0005;
    float f = smoothstep(-1.5, .5, (cos(a*5.)-sin(g)*.5))*(sin(g)-cos(g))*0.25+h;

    vec3 color = vec3(1.-smoothstep(f,f+0.05,r));

 	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float a = iResolution.y/iResolution.x;
    vec2 st = vec2(fragCoord.x/iResolution.x, fragCoord.y/iResolution.x);

    vec3 color = vec3(0., 0., fract(sin(dot(st.xy ,vec2(8989.9898,7878.8787))) * 43758.5453)*.56-st.y);

    color += star(vec2(0.17, 0.52*a)-st, 1.);
    color += star(vec2(0.3, 0.59*a)-st, 1.);
    color += star(vec2(0.41, 0.58*a)-st, 4.);
    color += star(vec2(0.54, 0.56*a)-st, 6.);
    color += star(vec2(0.63, 0.40*a)-st, 2.);
    color += star(vec2(0.82, 0.43*a)-st, 5.);
    color += star(vec2(0.83, 0.65*a)-st, 3.);

    fragColor += vec4(color.xyz, .89);
}
