//based on: https://www.shadertoy.com/view/wsyyRm

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //center pixel coordinates
    vec2 uv = fragCoord - (iResolution.xy / 2.);

    //get gradient starting at center (looks like a 1ps dot since anything above dist of 1 will be white)
    float dist = distance(uv, vec2(0.0));

    //use the gradient to generate waves
    dist *= 0.01; //make value smaller for larger waves
    float waves = sin(dist - iTime * .5);

    vec3 col = vec3(waves);

    col.b *= waves;    
    col.r = smoothstep(waves, 0.2, cos(iTime));
    col.g = smoothstep(waves, sin(uv.x)*cos(uv.y),0.9);
    col *= smoothstep(waves, sin(uv.x/uv.y),0.1);

    col.bg += smoothstep(waves, 0.9, cos(iTime))*0.5;
    col.r *= step(waves, 0.1);


    fragColor = vec4(col,1.0);
}
