void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xy;
    float t = iTime;
    float x = (p.x-0.5)*16.0;
    float y = (p.y-0.5)*9.0;
    float x2 = x*x;
    float y2 = y*y;
    vec2 center = vec2(0.5,0.5);
    float r = sin(1.0/sqrt((abs(x)*abs(y)))+8.0*t);
    float g = sin(1.0/sqrt((abs(x)*abs(y)))+9.0*t);
    float b = sin(1.0/sqrt((abs(x)*abs(y)))+10.0*t);
    vec3 col = vec3(r,g,b);

    fragColor = vec4(col,1.0);
}
