vec4 map(float d, vec2 fragCoord, float colorDepth) {
    vec2 center = floor(fragCoord.xy / d) * d;
    vec2 uv = center/iResolution.xy;
    vec2 diff = fragCoord - center;
    vec4 color = floor(texture(iChannel0, uv) * colorDepth) / colorDepth;

    if (diff.y < 2.0) {
        return vec4(0.0, 0.0, 0.0, 1.0);
    } else if (diff.x > d / 3.0 * 2.0) {
        return vec4(color.r, 0.0, 0.0, 1.0);
    } else if (diff.x > d / 3.0) {
        return vec4(0.0, color.g, 0.0, 1.0);
    } else {
        return vec4(0.0, 0.0, color.b, 1.0);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    // Output to screen
    fragColor = map(8.0, fragCoord + vec2(0.0, 0.0), 8.0) * 0.2 +
        map(8.0, fragCoord + vec2(2.0, 0.0), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(0.0, 2.0), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(-2.0, 0.0), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(0.0, -2.0), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(1.5, 1.5), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(-1.5, 1.5), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(1.5, -1.5), 16.0) * 0.1 +
        map(8.0, fragCoord + vec2(-1.5, -1.5), 16.0) * 0.1;
}
