//	# https://copy.sh/life/
//	# http://tlrobinson.net/blog/2009/02/game-of-life-generator/
//
/////////////////////////////////////////////////////////////  


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
 	fragColor = texture( iChannel0, fragCoord/iResolution.xy, 0.);
    fragColor.b = .2;
}

