// Brasil/Amazonas/Manaus                                       
// Game of Life: Text Generator!                                     
// Created by Rodrigo Cal (twitter: @rmmcal) - 2019/10          
/////////////////////////////////////////////////////////////
// -----------------------------------------------------------
// 
// 1) Necessary load the texture from external url and fullscreen
//  
//  - Click on Buffer A and after run command below to load the texture from url...  
//   
//  => gShaderToy.SetTexture(1,{mSrc:'https://i.imgur.com/LX3YL9w.png',mType:'texture',mID:1,mSampler:{filter:'mipmap',wrap:'repeat',vflip:'true',srgb:'false',internal:'byte'}});
//
//  the texture is the "Pattern" that will run on "Game of Life"
//     ===> https://i.imgur.com/LX3YL9w.png or https://i.imgur.com/Qp7qcmh.png
//
//     ===> The Pattern must print "Cal" :)
// 
// 2) Click full-screen and click on screen to start/reset and simulation.
//
// -----------------------------------------------------------
//
//  ===> Tutorial Custom texture by @AntoineC
//  ---------------------------> https://www.shadertoy.com/view/lsGGDd
//
// The idea is to call directly the SetTexture function found in Shadertoy js code.
// 
// Here is how to loads the three textures needed for this shader:
//  - Open the javascript console of your browser:
// 				   Mac      /     Windows
// 	Chrome:  cmd + opt + J  /  ctrl + shift J
// 	Firefox: cmd + opt + K  /  ctrl + shift K
//     IE:          na         /  F12   
// 
// Command to load the texture:
//
//  => gShaderToy.SetTexture(1,{mSrc:'https://i.imgur.com/Qp7qcmh.png',mType:'texture',mID:1,mSampler:{filter:'mipmap',wrap:'repeat',vflip:'true',srgb:'false',internal:'byte'}});
//
/////////////////////////////////////////////////////////////  
//  
// Inspiration :
//  - https://www.shadertoy.com/view/WsV3zW @FabriceNeyret2
//  - https://www.shadertoy.com/view/lsGGDd @AntoineC        => Custom texture <=
//
//  # https://bitstorm.org/gameoflife/
//	# https://copy.sh/life/
//	# http://tlrobinson.net/blog/2009/02/game-of-life-generator/
//
/////////////////////////////////////////////////////////////  

int isLife(ivec2 p){
	return texelFetch( iChannel0, p, 0).x != 0. ? 1 : 0;
}   


vec4 getBackbuffer(ivec2 p){
	return texelFetch( iChannel0, p, 0);
} 

vec4 getTexturePattern(ivec2 p){
	return texelFetch( iChannel1, p, 0);
} 



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 r = iResolution.xy;
    vec2 p = fragCoord / iResolution.xy;
    ivec2 ir = ivec2(iResolution.xy);
    ivec2 ip = ivec2(fragCoord);

    /////////////////////////////////////////////////
    /////////////////////////////////////////////////
    // Game of Life: logic

    int count = 0;
    for (int i = 0; i < 9; i++){
        //count neighbors with life
        if(i!=4) count += isLife( ip + ivec2(i%3-1, i/3-1) );
    }

    if ( isLife( ivec2(fragCoord) ) == 1){ 
        //keep life if (count < 2 || count > 3) else die
        fragColor = vec4((count < 2 || count > 3) ? 0 : 1); 
    }else{
        //keep dead if (count != 3) else resurrects
        fragColor = vec4((count != 3) ? 0 : 1); 
    }

    //
    /////////////////////////////////////////////////
    /////////////////////////////////////////////////
    // onResolutionChange and Mouse interaction below

    ivec2 varIdBackResolution = ivec2(0,0);
	vec4 rawRes = getBackbuffer(varIdBackResolution); // load
    bool onResolutionChange = (rawRes.x ) != r.x/1024.  ? true : false;

    if ( ( iMouse).z > .5 || onResolutionChange ) // reset simulation
    { 
        ivec2 translate = ivec2(1024 -int(r.x), 1024-int(r.y));
        fragColor = vec4(getTexturePattern(ip + translate ) );
    }

    ivec2 posPixelCheck = ivec2(23,1024-166); // position pixel check if loaded
    if (ip == varIdBackResolution && getTexturePattern(posPixelCheck).x > 0. ) // save
    {
        fragColor = vec4(r.x /1024. ); // save last resoluction
    }

}
