vec2 rotate(vec2 v, float a) {
	float s = sin(a);
	float c = cos(a);
	mat2 m = mat2(c, -s, s, c);
	return m * v;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float s = 10.0;
    vec2 uv = (fragCoord * s - 0.5 * s * iResolution.xy) / iResolution.xy;
    vec2 uvr = rotate(uv, iTime / 4.0);
    vec2 uvl = rotate(uv, -iTime / 4.0);
    for (int i = 0; i < 5; i++) {
      uvl += uv * dot(uvl, cos(uvr)) / 50.0;
      uvr += uv * dot(uvr, sin(uvl)) / 50.0;
    }
    vec3 col = 1.0 - pow(tan(uvr.yxy) + tan(uvl.yxx / 2.0) + tan(uvr.yxy / 3.0), vec3(1.0,1.0,1.0) * 1.5) * 0.15;
    fragColor.rgb = col;
}
