void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	vec2 uv1 = fragCoord.xy / iResolution.xy;
    vec2 uv2 = fragCoord.xy / iResolution.xy/10.0;
    //vec4 valA = vec4(1.0, 1.0, 1.0, 1.0);
	//vec4 valB = vec4(1.0, 1.0, 0.5, 1.0);
   	vec4 ramp = vec4(distance(uv1.x, uv2.y), 1.0, 1.0, 1.0);
    uv1.x = sin(uv1.x) * 2.0;
    uv1.y = -cos(uv1.y) * 3.0;
    uv1.x = iTime / 5.0 + uv1.x;
	uv1.y = iTime / 5.0 + uv1.y;   	

    vec4 A = texture(iChannel0,uv1 / 10.0);
    vec4 B = texture(iChannel0,-uv2 / 2.0);


    // Output to screen
    fragColor = mod(A, B) / ramp;
}

