void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Output to screen
    fragColor = texture(iChannel0,uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel1,((fragCoord)/iResolution.xy-0.5)*1.05+0.5);
    for(float x = 0.; x < 15.; x += 1.){
        for(float y = 0.; y < 15.; y += 1.){
    		vec4 t = texture(iChannel0, vec2(x,y)/iResolution.xy);
            if(abs((t.x+0.5)*iResolution.x-fragCoord.x)<1.&&abs((t.y+0.5)*iResolution.y-fragCoord.y)<1.){
            	fragColor = vec4(1.);
            }
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 p )
{
    if(p.x>15.||p.y>15.){
    	return;	
    }
    vec4 f = texture(iChannel0,p/iResolution.xy);
    f.x += sin(f.y*10.+p.y+p.x)/100.;
    f.y += sin(f.x*10.+p.y-p.x)/100.;
    fragColor = f;
}
