// INSTRUCTIONS:

// Click/drag to invert cell states (dead --> live, live --> dead)
// Auto-pauses when you draw
// Pause shader to prolong pause for lengthier drawings
// Restart project to generate random soup.
// I recommend screen zoomers to draw literally anything.

void mainImage(out vec4 a,in vec2 b){a=texture(iChannel0,b/iResolution.xy);}

float grab(vec2 uv, vec2 rel){
    return texture(iChannel0,uv+(rel/iResolution.xy)).r;
}

vec3 mPrev = vec3(0.);

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    mPrev = iMouse.xyz;
    vec2 uv = fragCoord/iResolution.xy;
    if(iFrame < 1){
    	fragColor = vec4(texture(iChannel1,fragCoord/256.).r>.5?1.:0.);
    }else{
        float a = float(round(texture(iChannel0,uv).r));
        if(iMouse.z > 0.){
            vec2 v1 = iMouse.xy-fragCoord;
            if(abs(v1.x)<1. && abs(v1.y)<1.){
            	a = 1.-a;
            }
        }else{
            int b = -int(a);
            for(int i=-1; i<2; i++){
                for(int j=-1; j<2; j++){
                	b += int(grab(uv, vec2(i,j)));
                }
            }
            if(a==1.){
                if(b<2||b>3){
                    a=0.;
                }
            }else{
                if(b==3){
                    a=1.;
                }
            }
        }
        fragColor = vec4(a);
    }
}
