void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0)
        * texture(iChannel1, fragCoord/iResolution.xy);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(iTime < 1.0)
        fragColor = vec4(0);
    else
    {
        float c = texelFetch(iChannel0, ivec2(fragCoord), 0).x;
        float d = 20.0 * length(iMouse.xy - fragCoord)/iResolution.y;
        //fragColor = vec4(c + clamp(1.0 - 5.0 * d, 0.0, 1.0));
        float e = exp(-d*d);
        //fragColor = vec4(max(c, e));
        fragColor = vec4(1.0 - (1.0 - c) * (1.0 - e));
    }
}
