mat2 rotate(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, s, -s, c);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec2 p = uv - 0.5;
    vec2 p2 = rotate(p.x + iTime + p.y) * p;

    float pattern = fract(8.5 * pow(p2.y, 0.5) *
        fract(p.y * 2.2 + fract(1.75 * fract(p2.x * 1.6) *
        pow(p.x * 1.8 + fract(p.y * 1.5 + iTime * 0.7 + p.x), 1.2))) + iTime * 0.4);

    pattern = smoothstep(0.48, 0.52, pattern);

    fragColor = vec4(vec3(pattern), 1.0);
}
