float spd=50.0;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 st=fragCoord;
   float w = 10.0;
   vec2 elapsed = vec2(floor(iTime*spd/w));
   vec2 f_st=floor(st/w);

   if(f_st.x<=elapsed.x && f_st.y<=elapsed.y)
   {
       st.x = mod(floor(st.x/w),2.0);
       st.y = mod(floor(st.y/w),2.0);
   }
   else
   {
       st.x=0.0;
       st.y=0.0;
   }

   fragColor=vec4(st,0.0,1.0);
}
