const float minX = -0.25;
const float maxX = 0.25;

float spark(float v){
    return ((1.0-abs(v/0.5))-0.5) * float(v>minX && v<maxX);
}

float rand(float v){
    return fract(sin(v)*43758.5453123);
}

vec4 meteorShowerLinear(vec2 fragCoord){
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float y = spark(uv.x-0.5-iTime + rand(uv.y)*100.0);

    return vec4(y);
}

vec2 curving(vec2 fragCoord){
    return vec2(fragCoord.x, fragCoord.y+fragCoord.x/2.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = meteorShowerLinear(curving(fragCoord));
}
