void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = (fragCoord.xy - .5*iResolution.xy)/iResolution.y;


    float maxIteration = 100.;//min(100., iTime*5.);
    maxIteration = (sin(iTime*2.)+1.)*50.;
    vec2 z = vec2(0.);
    vec2 c = uv*1.5;
    c -= vec2(.5,0.);
    float iteration = 0.;

    for(float i=0.; i<maxIteration; i++) {
        float x = z.x, y = z.y;

        z = vec2(x*x - y*y, 2.*x*y) + c;

        if(length(z)>2.) break;

        iteration++;
    }

    float f = iteration/maxIteration;
    vec3 col = vec3(f);

    if (f < 1.) {
        col = texture(iChannel0, vec2(f, 0.)).rgb;
    } else {
        col = vec3(0.);
    }
    // Output to screen
    fragColor = vec4(col,1.0);
}
