void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord/iResolution.y) - vec2(iResolution.x / iResolution.y, 1.0);
    uv *= 1.4;

    float pro = 0.0;

    const int particleCount = 20;

    for (int i = 0; i < particleCount; i++)
    {    
        float fI = float(i);
        float age = mod(iTime + fI / float(particleCount), 1.0);

        vec2 params = texture(iChannel1, vec2(
            (iTime - age) / iChannelResolution[1].x, 
            fI / iChannelResolution[1].y
        )).xy;

        vec2 pos = vec2(
        	(1.0 / (age + 1.0) - 1.0) * (params.x - .5) * 1.3,
            age / (2.0 - age) * 1.3 / (params.y * .5 + .5) - 1.0
        );

        float size = (1.0 - age) * .5;
        float sizeSqr = size * size;

        vec2 relativePos = uv - pos;
        float distSqr = relativePos.x * relativePos.x + relativePos.y * relativePos.y;

        float add = (sizeSqr / distSqr + size * 1.4) * (1.0 - age * age) * .5;
        pro += (add - mod(add, .5));
    }

    vec3 col = vec3((pro > 3.5) ? pro / 8.0 : 0.0, pro > 9.0, pro > 30.0);
    fragColor = vec4(col, max(col.x, col.y));
}
