// Line thickness
#define LINE_THICKNESS 8

float bracketiseNumber (float color)
{
    if (color < 0.1)
    	return 0.1;
    else if (color < 0.2)
        return 0.2;
    else if (color < 0.3)
        return 0.3;
    else if (color < 0.4)
        return 0.4;
    else if (color < 0.5)
        return 0.5;
    else if (color < 0.6)
        return 0.6;
    else if (color < 0.7)
        return 0.7;
    else if (color < 0.8)
        return 0.8;
    else if (color < 0.9)
        return 0.9;
    else
        return 1.0;
}

vec4 bracketiseColor (vec4 color)
{
    return vec4 (bracketiseNumber (color.r), bracketiseNumber (color.g), bracketiseNumber (color.b), 1.0);
}

vec4 outline (sampler2D tex, vec2 fragCoord)
{
    // Edge detection matrix [0,1,0],[1,-4,1],[0,1,0]
    // Colors
    vec4 up = texture (tex, (fragCoord + vec2 (0, LINE_THICKNESS))/iResolution.xy);
    vec4 left = texture (tex, (fragCoord + vec2 (-LINE_THICKNESS, 0))/iResolution.xy);
    vec4 center = texture (tex, fragCoord/iResolution.xy);
    vec4 right = texture (tex, (fragCoord + vec2 (LINE_THICKNESS, 0))/iResolution.xy);
    vec4 down = texture (tex, (fragCoord + vec2 (0, -LINE_THICKNESS))/iResolution.xy);

    // Return edge detection
    vec4 edgeDetection = up + left - 4.0*center + right + down;
    float edges = max (0.0, edgeDetection.r + edgeDetection.g + edgeDetection.b - 0.25);

    return vec4 (edges, edges, edges, 0.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //
    fragColor = bracketiseColor (texture (iChannel0, uv)) - outline (iChannel0, fragCoord);
}
