float N21Old(vec2 p) {
   return fract( sin(p.x * 100. + p.y*6574.)*5647.);
}

float N21(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx*1500.) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float SmoothNoise(vec2 uv) {
   vec2 cv = fract(uv);
   vec2 id = floor(uv);
   cv = cv*cv*(3.-2.*cv);


   float bl = N21(id);
   float br = N21(id+vec2(1, 0));
   float b = mix(bl, br, cv.x);

   float tl = N21(id+vec2(0, 1));
   float tr = N21(id+vec2(1, 1));
   float t = mix(tl, tr, cv.x);
   return mix(b, t, cv.y);
}

float SmoothNoise2(vec2 uv, float nBase) {
   float c = SmoothNoise(uv*nBase);
   c += SmoothNoise(uv*(2.*nBase)) * .5;
   c += SmoothNoise(uv*(4.*nBase)) * .25;
   c += SmoothNoise(uv*(8.*nBase)) * .125;
   c += SmoothNoise(uv*(16.*nBase)) * .0625;

   return c / 1.9;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;
   uv += iTime * .4;
   float c = SmoothNoise2(uv, 4.);

   vec3 col = vec3(c);

   fragColor = vec4(col,1.0);
}
