void mainImage( out vec4 Q, in vec2 U )
{
    vec4 
        n = C(U+vec2(0,1)),
        e = C(U+vec2(1,0)),
        s = C(U-vec2(0,1)),
        w = C(U-vec2(1,0));
    vec3 no = normalize(vec3(
        dot(e,vec4(1))-dot(w,vec4(1)),
        dot(n,vec4(1))-dot(s,vec4(1)),
        1));
    Q = 2.*atan(C(U));
    Q = .5-.5*(sin(Q.x*vec4(4,3,2,1)+Q.z*vec4(1,2,3,4)));
    Q = Q*(1.-0.5*texture(iChannel1,no));
    //Q = A(U);
}

void mainImage( out vec4 Q, in vec2 U )
{
    vec4 q = Q = A(U);
    vec4 c = C(U)*K;
    Q.x += (1.0+c.w)*(+c.x+c.y-q.y)*(1.-Q.x)*c.x;
    Q.y += (1.0+c.y)*(c.z+c.w-q.x)*(1.-Q.y)*c.z;
    if (Q.y>1.)Q.y*=0.;
    if (Q.x>.9)Q.x*=0.;
    Q.xy = clamp(Q.xy*.999,0.,1.);
    if (iMouse.z>0.&&length(U-iMouse.xy)<30.) Q.xy = 0.5+0.5*vec2(cos(iTime),sin(iTime));
    if (iFrame < 1){
        Q = vec4(0);
        if (length(U-0.75*R) < 2.) Q.x = 1.;
        if (length(U-0.25*R) < 2.) Q.y = 1.;
    }
}

