int shift(int v, int s){
    if ( s == 0) return v;
    if ( s == 8) return v/256;
    if ( s == 16) return v/256/256;
    if ( s == 24) return (v/256)/256/256;
    return 0;
}

int modi(int a, int n){
	return a - (n * int(a/n));
}
vec4 char( vec2 p, int v){ 
    vec2 v2 = clamp(p / 16., 0.0, 1./16.) -vec2(0.,1./16.) + vec2(float(modi(v,16))/16., -float(v/16)/16.);
    return vec4(texture(iChannel3,v2,-1.));                     
}

int map(ivec4 c, int i) // c[i] // compatibility function
{
	if (i == 0) return c.x;
	if (i == 1) return c.y;
	if (i == 2) return c.z;
	if (i == 3) return c.w;
    return 0;
}

vec4 mapText(vec2 p, vec2 tp, float s, ivec4 text) { 
    vec4 chr = vec4(0.);
    int k = 0;
    p -= tp;
    p+=vec2( .5/s );
    p *= vec2(s);
    int ip = 0;
    float len = 0.;
    for ( int i = 0; i < 20; i++){
        int chv = map(text,k);
        if (chv == 0) break; 					 		
        if (ip == 4) { k++; ip=0;} 
        else {
            int ch =  modi((shift(chv, 8*(3-(modi((ip),4))))),256); 
            if (ch == 0) { ip++;}
            else {
                chr = max(chr,  char(p, ch).x);           
                len++; ip++;
                p-=vec2(float(.5),0.);				
            }                
        }
    }  
    return vec4(chr.xyz, len);                   
}  

///////////

const float e =  2.718281828459045235360287;
const float PI = 3.14159265359;

void drawScene1(inout vec3 c, vec2 uv,float zoom){
    float powxy  = pow(uv.x, e);
    float powyx  = pow(e, uv.x);

    c = mix(c, vec3(0.,0.,1.), (1.0 - smoothstep(0.0, 0.4*zoom, abs(powxy - uv.y))));
	c = mix(c, vec3(1.,0.,0.), (1.0 - smoothstep(0.0, 0.4*zoom, abs(powyx - uv.y))));
}

void drawScene2(inout vec3 c, vec2 uv,float zoom, float alpha){

 	float x1 = 0.;

    /*******
	// TODO: calculates formula by points (x,y):

    (1.1, 43.55774)
    (1.2, 19.57631)
    (1.3, 12.52457)
    (1.4, 9.26136)
    (1.5, 7.408)
    (1.6, 6.22474)
    (1.7, 5.40687)
    (1.8, 4.81)
    (1.9, 4.35618)
    (2.0, 4.0)
    (2.1, 3.71329)
    (2.2, 3.47769)
    (2.3, 3.28074)
    (2.4, 3.11371)
    (2.5, 2.97028)
    (2.6, 2.84581)

	/// x1 = equation/formula ????????????????????? :(   anyone??????? 

	// i am using x1 as ~= ((pow(e-1.,2.) ) / max(0., uv.x-1.))+1. ;    // empiric mode.... :(


	/********/
	x1  = ((pow(e-1.,2.) ) / max(0., uv.x-1.))+1. ; 
    //  ..... equation error....  
    //  ..... incomplete.... 
    //  ..... approximate.....
	/********/

    float x2  = (uv.x );

    if (uv.x > 0.)
    {
        float minx = min (x1,x2);
        float maxx = max (x1,x2);

        c = mix(c, vec3(1.,.5,0.), alpha*.2*(1.0 - smoothstep(0.0, 0.01*zoom, max(-(maxx - uv.y), (minx - uv.y)))));

        c = mix(c, vec3(1.,0.,0.), alpha*(1.0 - smoothstep(0.0, 0.4*zoom, abs(minx - uv.y))));

        c = mix(c, vec3(0.,1.,0.), alpha*(1.0 - smoothstep(0.0, 0.4*zoom, abs(maxx - uv.y))));
    }
}
float sdBox( in vec2 p, in vec2 b )
{
    vec2 d = abs(p)-b;
    return length(max(d,vec2(0))) + min(max(d.x,d.y),0.0);
}

void drawPoints(inout vec3 c, vec2 uv, vec2 pa, vec2 mm, float zoom, float alpha){

    c = mix(c, vec3(1.0,0.,0.), alpha*smoothstep(-0.,0.01,.03-length((uv-mm.xx)/pa)));
    c = mix(c, vec3(0.0,1.,0.), alpha*smoothstep(-0.,0.01,.03-length((uv-mm.xy)/pa)));
  	c = mix(c, vec3(1.0,0.,0.), alpha*smoothstep(-0.,0.01,.03-length((uv-mm.yx)/pa)));
    c = mix(c, vec3(0.0,1.,0.), alpha*smoothstep(-0.,0.01,.03-length((uv-mm.yy)/pa)));

    vec2 ss = vec2(0.5)*(mm.yy-(mm.xx));

    float s = 0.05;
    vec2 u = uv/pa;

    u.xy +=iTime*.1 * cos((mm.xx)).x;
    alpha *= clamp(cos(PI*sqrt(length(uv/pa-(uv-mm.xx-ss)/pa))-iTime*3.),0.,1.);

    c = mix(c, vec3(0.5,1.,2.5), alpha*smoothstep(-0.,0.3*zoom,.2*zoom-abs(sdBox((uv-mm.xx-ss)/pa,(ss)/pa))));

    c = mix(c, vec3(1.0,0.,1.), alpha*smoothstep(-0.,0.01*zoom,.02*zoom-abs(sdBox((uv-mm.xx-ss)/pa,(ss)/pa))));

    alpha *= step(0.0, sign((mod(u.x , s) - s*.5 ) * (mod(u.y , s) - s*.5)));	
	c = mix(c, vec3(0.0,0.,1.), alpha*smoothstep(-0.,0.01*zoom,.02*zoom-abs(sdBox((uv-mm.xx-ss)/pa,(ss)/pa))));

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= .5;
 	uv *= vec2(iResolution.x/iResolution.y,1.);

    float tt = iTime*.05;
    float t = ((fract(tt*.5))*2.-1.);
    float t0 = abs((fract(tt/8.+4./8.))*2.-1.);
    float t1 = abs(t);
    float t2 = pow((t1),4.)*.999+.001;
    float zoom = pow((cos((t1+1.)*PI)+1.)/2.,2.)*.5 + .001;
    uv *= 8. * zoom;

    float ts = t0;
    vec2 pa1 = vec2(1.,15.); 
    vec2 pa2 = vec2(2.,10.); 

    vec2 pa = mix(pa1,pa2,step(.5, ts)); 

    uv *= pa;
    vec3 c = vec3(1.0);

    vec2 pc1 = vec2(e,pow(e,e));
    vec2 pc2 = vec2(e,e);

	uv += mix(pc1,pc2,step(.5, ts));  

    c = mix(c, vec3(.8), (1.0 - smoothstep(0.0, .4/pa.y, abs(uv.x-floor(uv.x)))));
    c = mix(c, vec3(.8), (1.0 - smoothstep(0.0, .4/pa.x, abs(uv.y-floor(uv.y)))));

    c = mix(c, vec3(0.), (1.0 - smoothstep(0.0, 0.4/pa.y, abs(uv.x))));
	c = mix(c, vec3(0.), (1.0 - smoothstep(0.0, 0.4/pa.x, abs(uv.y))));

    drawScene1(c, uv, zoom);
	drawScene2(c, uv, zoom, ts);

    c = mix(c, vec3(.7,0.4,0.4), (1.0 - smoothstep(0.0, step(0.5,mod(uv.y*10./pa.y,1.))*0.4*zoom/pa.y, abs(uv.x-e))));
	c = mix(c, vec3(.7,0.4,0.4), (1.0 - smoothstep(0.0, step(0.5,mod(uv.x*10./pa.x,1.))*0.4*zoom/pa.x, abs(uv.y-pow(e,e)))));
	c = mix(c, vec3(.8,0.8,0.1), ts*(1.0 - smoothstep(0.0, step(0.5,mod(uv.x*10./pa.x,1.))*0.4*zoom/pa.x, abs(uv.y-(e)))));

    c = mix(c, vec3(0.), .2*smoothstep(-0.0001,0.0,-abs(.005-length((uv-vec2(e,pow(e,e)))/pa))));
    c = mix(c, vec3(0.), smoothstep(-0.0001,0.0,-abs(.001-length((uv-vec2(e,pow(e,e)))/pa))));


    float ts1 = 1.-step(.5, ts);
  	float ts2 = step(.5, ts);

    {
        c = mix(c, vec3(1.0,1.,0.), smoothstep(-0.,0.005,.003-length((uv-vec2(e,e))/pa)));
    	c = mix(c, vec3(0.), .2*smoothstep(-0.0001,0.0,-abs(.005-length((uv-vec2(e,e))/pa))));
    	c = mix(c, vec3(0.), smoothstep(-0.0001,0.0,-abs(.001-length((uv-vec2(e,e))/pa))));

        float ts0 = step(.8, ts)*ts*.5;

        drawPoints(  c,  uv,  pa,  vec2(1.1, 43.55774), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(1.2, 19.57631), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(1.3, 12.52457), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(1.4, 9.26136), zoom, ts0);
        //drawPoints(  c,  uv,  pa,  vec2(1.5, 7.408), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(1.6, 6.22474), zoom, ts0);
        //drawPoints(  c,  uv,  pa,  vec2(1.7, 5.40687), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(1.8, 4.81), zoom, ts0);
        //drawPoints(  c,  uv,  pa,  vec2(1.9, 4.35618), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(2.0, 4.0), zoom, ts0);
        //drawPoints(  c,  uv,  pa,  vec2(2.1, 3.71329), ts0);
        drawPoints(  c,  uv,  pa,  vec2(2.2, 3.47769), zoom, ts0);
        //drawPoints(  c,  uv,  pa,  vec2(2.3, 3.28074), zoom, ts0);
        drawPoints(  c,  uv,  pa,  vec2(2.4, 3.11371), zoom, ts0);
        //drawPoints(  c,  uv,  pa,  vec2(2.5, 2.97028), zoom, ts0);

        drawPoints(  c,  uv,  pa,  vec2(2.6, 2.84581), zoom, ts0);
    }

  	c = mix(c, vec3(0.0,0.,1.), ts1*mapText(uv/pa, vec2(e*5./6.,pow(e,e))/pa, 2./zoom, ivec4(0x785e79,0,0,0)).x);
  	c = mix(c, vec3(1.0,0.,0.), ts1*mapText(uv/pa, vec2(e*5./6.,pow(e,e)+e)/pa, 2./zoom, ivec4(0x795e78,0,0,0)).x);

  	c = mix(c, vec3(0.0,0.,1.), ts1*mapText(uv/pa, vec2(e*4./6.,pow(e,e))/pa, 2./zoom, ivec4(0x785e65,0,0,0)).x);
  	c = mix(c, vec3(1.0,0.,0.), ts1*mapText(uv/pa, vec2(e*4./6.,pow(e,e)+e)/pa, 2./zoom, ivec4(0x655e78,0,0,0)).x);

  	c = mix(c, vec3(0.1), ts1*mapText(uv/pa, vec2(e,pow(e,e))/pa, 2./zoom, ivec4(0x28652c65,0x5e6529,0,0)).x);
	c = mix(c, vec3(0.1), ts2*mapText(uv/pa, vec2(e,e)/pa, 2./zoom, ivec4(0x28652c65,0x29,0, 0)).x);

    c = mix(c, vec3(0.1), ts2*mapText(uv/pa, vec2(e+e/2.,e)/pa, 2./zoom, ivec4(0x785e7920, 0x3e20795e, 0x78, 0)).x);

    c = mix(c, vec3(0.1), ts2*mapText(uv/pa, vec2(e-e/10.,e+e*0.8)/pa, 2./zoom, ivec4(0x785e7920, 0x3c20795e, 0x78, 0)).x);

    c = mix(c, vec3(0.1), ts2*mapText(uv/pa, vec2(e-e/10.,e-e*0.8)/pa, 2./zoom, ivec4(0x785e7920, 0x3c20795e, 0x78, 0)).x);

    c = mix(c, vec3(0.0,1.,0.), ts2*mapText(uv/pa, vec2(e+e,e+2.)/pa, 2./zoom, ivec4(0x6d617820, 0x72616e67, 0x65202879, 0x29)).x);
    c = mix(c, vec3(1.0,0.,0.), ts2*mapText(uv/pa, vec2(e+e,e-2.)/pa, 2./zoom, ivec4(0x6d696e20, 0x72616e67, 0x65202879, 0x29)).x);

    // Output to screen
    fragColor = vec4(c ,1.0);
}
