#define N 12.0
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = floor((fragCoord.xy)/iResolution.x*N);
    vec2 color = mod(uv.xx+uv.yy,2.0); // x+y even:black  odd:white

    // Output to screen
    fragColor = vec4(color.xxx,1);
}
