void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Number of lights in simulator
    int lights =20;
    float timespeed = 10.;


    float time = float(int(iTime*timespeed))/float(lights);
    uv.x = float(int(uv.x*float(lights)))/float(lights);


    // function for each color
    float r = abs(1.-2.*mod(1.-uv.x+time,1.));

    float g = 0.;

    float b = abs(1.-2.*mod(1.+uv.x+time,1.));

    vec3 col = vec3(1,0,0)*r;

    col += vec3(0,1,0)*g;

	col += vec3(0,0,1)*b; 

    // Output to screen
    fragColor = vec4(col,1.0);
}
