void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;
    float r = 0.3;

    vec2 pCoord = uv * 30.;
    float xWaveSize = (1. + sin(iTime*0.7)) * 2.;
    float yWaveSize = (1. + sin(iTime*0.5)) * 2.;
    pCoord.x += iTime + xWaveSize * cos(0.25 * pCoord.y + iTime*2.);
    pCoord.y += iTime + yWaveSize * sin(0.25 * pCoord.x + iTime*3.);
    float dist = length(fract(pCoord) - vec2(0.5, 0.5));
    float result = 1. - dist*3.;
    float py = smoothstep(r, r-0.2, result);

    vec3 col = vec3(uv.x * py, uv.y * py, (1.-uv.y) * py);

    fragColor = vec4(col,1.0);
}
