void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float col;
    float t = iTime*.1;
    vec2 uv = (fragCoord-iResolution.xy)/iResolution.y+vec2(t,t*2.0);
        float factor = 1.5;
        for(int i=0;i<9;i++)
        {
            uv *= -factor*factor;
            uv += sin(uv.yx/factor)/factor;
            col += sin(uv.x-uv.y+col)+cos(uv.y-uv.x);
        }
    fragColor = vec4(vec3(col/5.0),1.0);
}
