//parameters
const float stretch = .1;
const float revolutionsPerSecond = 1.;
const float topAmount = .98;
//colors
const vec4 red = vec4(1.,0.,0.,1.);
const vec4 green = vec4(0.,1.,0.,1.);
const vec4 blue = vec4(0.,0.,1.,1.);
const vec4 yellow = vec4(1.,1.,0.,1.);
const vec4 purple = vec4((red.xyz + blue.xyz)/2.,1.);
//displayed colors
const vec4 topColorA = purple;
const vec4 topColorB = green;
const vec4 bottomColorA = purple;
const vec4 bottomColorB = green;

//I learned a bit late about GLSL's "mix" function
vec3 lerp(in vec3 colorA, in vec3 colorB, float val)
{
    return colorA * (1. - val) + colorB * val;
}
//scrolls and stretches a pixel along the x coordinate of the screen
float scrollAndStretch(float x, float stretch, float revolutionsPerSecond)
{
    float speed = revolutionsPerSecond * iResolution.x;
    return mod((x + speed * iTime) * stretch, iResolution.x);
}
//splits the screen in half (lengthwise) between color A and color B
vec4 splitScreenColor(vec4 colorA, vec4 colorB, float x)
{
    return x < iResolution.x/2. ? colorA : colorB;
}
//displays a smooth gradient from color A to color B and back again
vec4 gradient(vec4 colorA, vec4 colorB, float x)
{
    return vec4(lerp(colorA.rgb, colorB.rgb, abs(1. - 2. * x/iResolution.x)), 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if(fragCoord.y>iResolution.y * (1. - clamp(topAmount, 0., 1.))){
        fragCoord.x = scrollAndStretch(fragCoord.x, stretch, revolutionsPerSecond);
        fragColor = gradient(topColorA, topColorB, fragCoord.x);
        //fragColor = splitScreenColor(topColorA, topColorB, fragCoord.x);
    }
    else 
        fragColor = splitScreenColor(bottomColorA, bottomColorB, fragCoord.x);
}
