#define R iResolution.xy
#define Iterations 20.
mat2 r2(float angle) { return mat2(cos(angle), -sin(angle), sin(angle), cos(angle)); }

float sdCircle(vec2 p, float r) {
	return length(p) - r;    
}

vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ) {
    return a + b*cos(6.28318 * (c*t + d));
}

vec2 T(vec2 p) {

    vec4 w = texture( iChannel0, vec2(15./256.,0.1));
    float s = 0.0006 + 0.0005 * cos(iTime/2.);
    for(float i=0.; i < Iterations; i++) {
    	p = abs(p) - s - i/Iterations; 
        p *= r2(3.1415*fract(iTime / 12.) + w.x);
        p *= (i/Iterations*.4 + 1.);
    }

    return p;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.*fragCoord-R)/R.y;

    // Time varying pixel color
    vec3 col = pal(fract(0.3*sdCircle(T(uv), 0.5) + iTime / 7.), vec3(.5), vec3(0.5), 
                   vec3(1.0,1.0,1.0), vec3(.0, .10, .2));

    // Output to screen
    fragColor = vec4(col,1.0);
}
