// function for future, thinking about code optimisation 
// and more simple circle creation before positioning and coloring
vec3 colorFun(float posCoef, vec2 fragCoord){
    	vec2 pIn = (fragCoord.xy * 2. - iResolution.xy*posCoef) /iResolution.xy;
    	// ring
    	float tIn = 0.02 / abs(0.5 - length(pIn));
    	return vec3(tIn, 0.15, tIn);
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    // position
    vec2 p = (fragCoord.xy * 2. - iResolution.xy*1.024) /iResolution.y;
    // ring
    float t = 0.02 / abs(0.5 - length(p));
    vec3 color = vec3(t, 0.15, t);

    // position
    vec2 p1 = (fragCoord.xy*2. - iResolution.xy*0.808) /iResolution.y;
    p1.x = p1.x - 0.7; 
    // ring
    float t1 = 0.02 / abs(0.5 - length(p1));
    vec3 color1 = vec3(t1, t1, 0.15);

    // position
    vec2 p2 = (fragCoord.xy * 2.128 - iResolution.xy *1.568)/iResolution.y;
    // ring
    float t2 = 0.02 / abs(0.5 - length(p2));
    vec3 color2 = vec3(0.15, t2, t2);

    vec3 colorM = color + color1 + color2;
    colorM.x = colorM.x;

    fragColor = vec4( colorM , 1.0);
}
