void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float newTime = iTime*3.0;
    float uvt = 1.2;
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-iResolution.xy*0.5)/iResolution.y;
    uv += vec2(sin(newTime*0.1*uvt), cos(newTime*0.12*uvt))*0.18;
    uv += vec2(sin(newTime*0.1*1.2*uvt), cos(newTime*0.12*1.2*uvt))*0.02;
    uv *= 1.5;

    float mask = smoothstep(0.5, 0.0, length(uv));
    mask *= 1.0-(uv.y+0.5);

    float f = 10.0;

    float d = (uv.y+0.5)+1.0; // denser higher
    float fairy1 = 0.05*sin(dot(uv, vec2(sin(newTime*0.2),cos(newTime*0.15)))*10.5*d+newTime);
    fairy1 += 0.15*sin(dot(uv, vec2(sin(newTime*0.2+1.42),cos(newTime*0.15+1.46)))*6.5*d*d+newTime);

    fairy1 = fairy1*0.5+0.5;
    fairy1 *= mask;
    fairy1 += mask*0.7;

    fairy1 += smoothstep(0.4, 0.7, fairy1);

    newTime += 0.8;
    float fairy2 = 0.05*sin(dot(uv, vec2(sin(newTime*0.2),cos(newTime*0.15)))*10.5*d+newTime);
    fairy2 += 0.15*sin(dot(uv, vec2(sin(newTime*0.2+1.42),cos(newTime*0.15+1.46)))*6.5*d*d+newTime);
    fairy2 += 0.15*sin(dot(uv, vec2(sin(newTime*-0.2+2.42),cos(newTime*-0.2+2.42)))*20.5+newTime);
    fairy2 += 0.09*sin(dot(uv, vec2(sin(newTime*0.26+2.42),cos(newTime*0.26+2.42)))*16.5+newTime);

    fairy2 = fairy2*0.5+0.5;
    fairy2 *= mask;
    fairy2 += mask*0.7;

    fairy2 += smoothstep(0.52, 0.43, fairy2)*mask*mask;

    float twoFairies = clamp((fairy1 - fairy2*1.3)*1.0, 0.0, 1.0);

    float glow = mask*1.6;

    // Output to screen
    fragColor = vec4(vec3(glow)*vec3(1.8*glow,1.0*glow,0.8) + vec3(twoFairies)*vec3(1.6,1.0,0.2),1.0);
}
