// Gradient noise from Jorge Jimenez's presentation:
// http://www.iryoku.com/next-generation-post-processing-in-call-of-duty-advanced-warfare
float gradientNoise(in vec2 uv)
{
    const vec3 magic = vec3(0.06711056, 0.00583715, 52.9829189);
    return fract(magic.z * fract(dot(uv, magic.xy)));
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // #3E91DF
    vec2 uv = fragCoord/iResolution.xy;
	vec3 peaceBlue = vec3(0.243, 0.569, 0.875);
    vec3 peaceWhite = vec3(1.,1.,1.);

    vec3 final = mix(peaceBlue, peaceWhite, smoothstep(0.1, 0.9,uv.x));

    // Add ditheing to reduce banding.
    final += (1.0/255.0) * gradientNoise(fragCoord) - (0.5/255.0);

    // Output to screen
    fragColor = vec4(final,1.0);
}
