vec4 colorToGray( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy;
    vec4 texColor = texture(iChannel0,xy);
    float ponderedColor = ((texColor.r + texColor.g + texColor.b) / 3.0);
    float phase = abs(cos(iTime));
    texColor.r = ponderedColor + (texColor.r-ponderedColor)*phase;
    texColor.g = ponderedColor + (texColor.g-ponderedColor)*phase;
    texColor.b = ponderedColor + (texColor.b-ponderedColor)*phase;
    return texColor;
}

vec4 substituteGreenScreen ( vec4 base, vec4 objective ){
    float greenPercentage = (base.g) / (base.r+base.g+base.b);
    if(greenPercentage > 0.45){
        return objective;   
    }
    return base;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 xy = fragCoord.xy / iResolution.xy;
    fragColor = substituteGreenScreen(texture(iChannel1,xy), texture(iChannel0,xy));
}
