float DistLine(vec3 ro, vec3 rd,vec3 p) {
    return length(cross(p-ro, rd))/length(rd);
    // gets distance from point to the line where ro and rd lie

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy; // from 0 to 1
    uv -= 0.5; //shifts the center of the uv, range is normalized to -0.5 to 0.5
    uv.x *= iResolution.x/iResolution.y; //adjust the resolution to fit screen

    vec4 sampledColor = texture(iChannel0, uv); //gets texture


    vec3 ro = vec3(0.0, 0.0, -1.0); //ray origin coordinates AKA camera
    vec3 rd = vec3(uv.x, uv.y, 0.0)-ro; // ray direction, which is intersection point - ray origin (rd = i-ro)

    float t = iTime; //get changing numbers for movement

    vec3 p = vec3(-sin(t), 0.0, .5+cos(t)); // moving the point closer and farther to the camera 
    float d = DistLine(ro, rd, p); // get distance from function inputs; calculate distance from point

    vec3 c = smoothstep(0.1, 0.09, d) * sampledColor.rgb;
    //set texture of the alhpa which will be seen through the circle

	d = smoothstep(0.1, 0.08, d); //smoothly transitions the black background and white point, otherwise would have jagged discrete pixels


    fragColor = vec4(c,d); //output to screen


}
