void mainImage(out vec4 fragColor,in vec2 fragCoord) {
  vec3 d=vec3(gl_FragCoord.xy/iResolution.xy-.5,.8),
       p=vec3(0,sin(iTime*12.)/2e2,iTime),q;
  for(int i=0;i<99;i++){
    p+=d*min(.65-length(fract(p+.5)-.5),p.y+.2);
    if(i==50) {
      q=p,p-=d*.01,d=vec3(.7);
    }
  }
  ivec3 u=ivec3(q*5e2);
  fragColor=vec4((u.x^u.y^u.z)&255)/3e3*min(length(p-q)+1.,9.)+(p.z-iTime)*.1;
}
