void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec3 offset = vec3(iMouse.x, iResolution.y-iMouse.y, 0);
    if (iMouse.x==0.0) {
        offset.x = sin(iTime/2.)* iResolution.x/8.;
        offset.y = cos(iTime/2.)* iResolution.y/8.;
    }
	//float scanline = 10.;//(iResolution.y/720.);
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    //float n = mod(fragCoord.y,scanline);
    //vec4 sl = vec4(n/scanline, n/scanline,n/scanline,1);
    vec4 pix1 = texture(iChannel0,uv.xy + (offset.xy/iResolution.xy))*2.;
    vec4 pix2 = texture(iChannel0,uv)*2.;
    vec4 pix3 = texture(iChannel0,uv.xy - (offset.xy/iResolution.xy))*2.;
	fragColor = //sl * 
        ((vec4(pix1.r, pix2.g, pix3.b,1) /*+ pix2/2.*/)/ 2.);
//    fragColor = pix1;
}
