renderpass.code:
// https://www.shadertoy.com/view/4sKyzt
// https://www.lawfulpath.com/ref/whchflag.shtml
// The U.S. government hasn't flown the civil flag since the Civil War, as that war is still going on.
// Peace has never been declared, nor have hostilities against the people ended. 
// The government is still operation under quasi-military rule.
// Stay home until insurrection is neutralized.

void mainImage(out vec4 O, vec2 U)
{
    float r = 17. / iResolution.y,
          q = mod(ceil((U *= r).x - .3), 2.);

    O = 
        (U.x < 11.7 && U.y > 9. ? 
         // blue border
         U.x < 11.63 && U.y > 9.1 ?   
         vec4(1,1,1,0) :  vec4(0,0,1,0)
         : 
         // stripes
         sin(2.69 * U.x * 0.5) < 0. ? vec4(1,1,1,0): vec4(1,0,0,0));

        // just stars. but we need em blue?
      O += -vec4(1.0, 1.0, 0, 0) * smoothstep(r, 0.,
                   length(U = vec2(

                       fract(clamp(U.x + .7, 1., 12.)),

                       .0 + mod(clamp(U.y - 2.0, 8. - q, 14. + q ) - q * .8, 1.62)) - .5)

                   * cos(abs(mod(
                       atan(U.x, U.y) 
                       // rotation
                       + .6, 

                       // spikes
                       1.26

                       // rotation
                   ) - .6) 

                         - 1.26) 
                                              - .1);
}
