float dCircle(vec2 uv, vec2 p, float r){
    return length(uv-p)-r;
}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-(iResolution.xy*.5))/iResolution.y;
    vec2 mPos = (iMouse.xy-(iResolution.xy*.5))/iResolution.y;

    vec3 col = vec3(0);

    vec2 p = vec2(0);
    float f = 100.;
    float r = .015;
    float ms = iTime+200.;
    float n = 200.;
    vec3 vCol = sin(ms*vec3(1.72345623,1.32634234,1.736474))*.5+.51;
    vCol *= 10.;
    for(float i=1.; i<=n; i++){
        float rf = ms*i/n;
    	p += vec2(sin(rf),cos(rf))*i/f;

    	float d = dCircle(uv,p,r*rf*.1);

    	d = smoothstep(0.001, 0., d);
        d*=(1.-i/n)*(1.-i/n)*(1.-i/n)*(1.-i/n)*(1.-i/n)*(1.-i/n)*(1.-i/n);
        col += vCol*d*i/n;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
