void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 eye1Jitter = 0.01 * vec2(cos(iTime/2.0), sin(iTime/2.0));
    vec2 eye2Jitter = 0.01 * vec2(cos(iTime/4.0), sin(iTime/4.0));
    vec2 eye1Offset = vec2(0.0, 0.05) + eye1Jitter;
    vec2 eye2Offset = vec2(0.1, 0.05) + eye2Jitter;
    vec4 eye1 = texture(iChannel0, uv*0.9+eye1Offset);
    vec4 eye2 = texture(iChannel0, uv*0.9+eye2Offset);

    // Output to screen
    fragColor = eye1*0.5 + eye2*0.5;
}
